// Helper function to get project by slug
export const getProjectBySlug = (slug) => {
    return projects.find((project) => project.slug === slug);
};

// Helper function to get all project slugs (for static paths)
export const getAllProjectSlugs = () => {
    return projects.map((project) => project.slug);
};

// Helper function to get next project data
export const getNextProject = (currentSlug) => {
    const currentProject = getProjectBySlug(currentSlug);
    if (currentProject && currentProject.nextProject) {
        return getProjectBySlug(currentProject.nextProject);
    }
    return null;
};